try {
    var assayId = $('CurrentAssay').ID;

    logInfo('Retrieving assay configuration', {
        assayId: assayId,
        assayName: $('CurrentAssay').ASSAY_NAME,
        correlationId: getCorrelationId()
    });

    var dbJSON = QTYLink_DB().GetASSAYLIST(assayId);

    // Validate result
    if (!dbJSON || dbJSON.length === 0) {
        var error = new Error('No assay configuration found for assay ID: ' + assayId);
        handleError('GetASSAYLIST', error, {
            assayId: assayId,
            correlationId: getCorrelationId()
        });
        throw error;
    }

    logInfo('Assay configuration retrieved', {
        assayId: assayId,
        configItemCount: dbJSON.length,
        targets: dbJSON.map(function(item) { return item.TARGET; }).filter(function(v, i, a) { return a.indexOf(v) === i; }),
        correlationId: getCorrelationId()
    });

} catch (e) {
    handleError('GetASSAYLISTStep', e, {
        assayId: $('CurrentAssay').ID,
        correlationId: getCorrelationId()
    });
    throw e; // Stop processing
}

