// ------------- Functions ----------------

function getPartitions(type) {
    var getPlatesConfig = [ //get from DB reference 
        { "ID": 1, "NAME": "Nanoplate 26K 24-well", "PARTITIONS-NUM": 26384 },
        { "ID": 2, "NAME": "Nanoplate 26K 8-well", "PARTITIONS-NUM": 26384 },
        { "ID": 3, "NAME": "Nanoplate 8.5K 96-well", "PARTITIONS-NUM": 8510 },
        { "ID": 4, "NAME": "Nanoplate 8.5K 24-well", "PARTITIONS-NUM": 8510 }
    ];

    // Find the first matching item
    var foundPlate = getPlatesConfig.find(s => s.NAME === type);

    // Return the number of partitions if found, otherwise return 0
    return foundPlate ? foundPlate["PARTITIONS-NUM"] : 0;
}

/*
This part uses DB assay specifics as reference. For each sample in QTY output:
- Build a column-name-based index (robust to BOM/whitespace/case)
- Create a sample list using common headers
- Add assay-specific value extraction (MODALITY = "GetValue") matching target name
- (TBD) Add assay-specific calculations
*/

// ============================== Helpers ==============================
function stripBOM(s) { return (s || "").toString().replace(/^\uFEFF/, ""); }
function norm(s) { return stripBOM(s).trim().toLowerCase(); }

// Safe cell access: never do row[undefined]
function getCell(row, headerName) {
    var exactKey = columnIndexMapExact[headerName];
    var normKey  = columnIndexMapNorm[norm(headerName)];
    var key = exactKey || normKey;
    if (!key) return null;
    var node = row[key];
    if (!node || getArrayOrXmlLength(node) === 0) return null;
    return node.toString();
}

// ===================== Build header -> columnN maps ===================
var headers = msg.children()[0]; // First row = header names when CSV Reader keeps column1..N
if (!headers) {
    throw "CSV header row (index 0) not found. Check your CSV Reader settings.";
}

var columnIndexMapExact = {}; // exact: "Well"       -> "column1"
var columnIndexMapNorm  = {}; // norm : "well"       -> "column1"
var colIndex = 1;

for each (var column in headers.children()) {
    var raw = column.toString();
    var clean = stripBOM(raw).trim();
    var key = "column" + colIndex;
    columnIndexMapExact[clean] = key;
    columnIndexMapNorm[norm(clean)] = key;
    colIndex++;
}

// (décommente pour diagnostiquer)
// logger.info("Header map (exact): " + JSON.stringify(columnIndexMapExact));
// logger.info("Header map (norm) : " + JSON.stringify(columnIndexMapNorm));

// =================== Pre-index dbJSON (GetValue only) =================
var getValueByTarget = {}; // { "gus": [cfg, cfg2], "ic": [cfg] ... }
for each (var cfg in dbJSON) {
    if (!cfg || cfg.MODALITY !== "GetValue") continue;
    var t = norm(cfg.TARGET);
    if (!getValueByTarget[t]) getValueByTarget[t] = [];
    getValueByTarget[t].push(cfg);
}

// ============================ Extraction =============================
var extractedData = [];
var totalRows = getArrayOrXmlLength(msg.children());

// Loop through each data row (skip header row index 0)
for (var i = 1; i < totalRows; i++) {
    var row = msg.children()[i];

    // Base fields (safe access; "N/A" if missing)
    var sampleData = {
        "Well":                 getCell(row, "Well")                    || "N/A",
        "Sample_NTC_Control":   getCell(row, "Sample/NTC/Control")      || "N/A",
        "Type":                 getCell(row, "Type")                    || "N/A",
        "ReactionMix":          getCell(row, "Reaction Mix")            || "N/A",
        "Target_Name":          getCell(row, "Target (Name)")           || "N/A",
        "Target_IC":            getCell(row, "Target (IC)")             || "N/A",
        "Target_Ctr":           getCell(row, "Target (Control)")        || "N/A",
        "Values": []
    };

    var targetNorm = norm(sampleData.Target_Name);
    if (!targetNorm || targetNorm === "n/a") {
        // Pas de target exploitable: on log et on continue
        logger.warn("Row " + i + ": Target (Name) missing or N/A; skipping GetValue extraction.");
        extractedData.push(sampleData);
        continue;
    }

    var cfgList = getValueByTarget[targetNorm];
    if (!cfgList || cfgList.length === 0) {
        // Aucun mapping GetValue pour ce target → info utile
        // logger.info("Row " + i + ": No GetValue config for target '" + sampleData.Target_Name + "'.");
        extractedData.push(sampleData);
        continue;
    }

    // ----------------------- GetValue extraction -----------------------
    for each (var config in cfgList) {
        // Trouver la colonne demandée par la config (robuste)
        var requestedColNameRaw = (config.Column || "");
        var requestedColName = stripBOM(requestedColNameRaw).trim();
        var colKey = columnIndexMapExact[requestedColName] || columnIndexMapNorm[norm(requestedColName)];

        if (!colKey) {
            logger.warn("Row " + i + ": CSV column not found for config ID '" + config.ID + "' -> '" + requestedColName + "'");
            continue;
        }

        var node = row[colKey];
        if (!node || getArrayOrXmlLength(node) === 0) {
            logger.warn("Row " + i + ": Empty node for column '" + requestedColName + "' (config ID '" + config.ID + "')");
            continue;
        }

        var columnValue = node.toString();

        sampleData.Values.push({
            "configID": config.ID,
            "config":   config.CRITERIA,
            "Value":    columnValue
        });
    }

    extractedData.push(sampleData);
}

// ============================== Output ===============================
var finalJson = JSON.stringify(extractedData, null, 4);
// channelMap.put("finalExtractedJson", finalJson);
// logger.info("Extracted JSON:\n" + finalJson);

