		        var header = requiredHeaders[i];
		        if (!columnIndexMapNorm[norm(header)]) {
		            missingHeaders.push(header);
		        }
		    }

		    if (missingHeaders.length > 0) {
		        var error = new Error('Required CSV headers missing: ' + missingHeaders.join(', '));
