// OPTIMIZATION: Build indexed lookup for O(1) access instead of O(n) scanning
// This reduces complexity from O(n³) to O(n) for ratio calculations

try {
    logInfo('Building target data index', {
        sampleCount: extractedData.length,
        correlationId: getCorrelationId()
    });

    // Build index map: Key = "SampleName_TargetName" -> Value = entire sample object
    var targetDataIndex = {};
    var indexedCount = 0;

    for (var i = 0; i < extractedData.length; i++) {
        var sample = extractedData[i];

        if (!sample || !sample.Sample_NTC_Control || !sample.Well || !sample.Target_Name) {
            continue; // Skip invalid entries
        }

        // Create composite key: "SampleName_Well_TargetName"
        var key = sample.Sample_NTC_Control + '_' + sample.Well + '_' + sample.Target_Name;
        targetDataIndex[key] = sample;
        indexedCount++;
    }

    logInfo('Target data index built', {
        indexedItemCount: indexedCount,
        sampleCount: extractedData.length,
        correlationId: getCorrelationId()
    });

    // Store in channelMap for use in subsequent steps
    channelMap.put('targetDataIndex', JSON.stringify(targetDataIndex));

} catch (e) {
    handleError('BuildTargetIndexStep', e, {
        correlationId: getCorrelationId()
    });
    throw e;
}

// ============================================================================
// PERFORMANCE OPTIMIZATION: Build index for O(1) lookups in ratio calculations
// This eliminates repeated O(n) array scans in Steps 6, 7, 8
// ============================================================================

try {
    var correlationId = channelMap.get('correlationId') || 'unknown';

    logInfo('Building sample value index', {
        correlationId: correlationId,
        extractedDataSize: extractedData.length
    });

    // Build comprehensive index: key = "sampleID_Well_configID"
    var sampleValueIndex = {};
    var indexEntries = 0;

    for (var i = 0; i < extractedData.length; i++) {
        var sample = extractedData[i];
        var sampleID = sample.Sample_NTC_Control;
        var well = sample.Well;
        var targetName = sample.Target_Name;

        // Index each value in this sample's Values array
        for (var j = 0; j < sample.Values.length; j++) {
            var valueObj = sample.Values[j];
            var key = sampleID + '_' + well + '_' + valueObj.configID;

            sampleValueIndex[key] = {
                config: valueObj.config,
                Value: valueObj.Value,
                Target: targetName,
                sampleID: sampleID,
                well: well
            };
            indexEntries++;
        }
    }

    // Store in channelMap as JSON string for access in Steps 6-8
    channelMap.put('sampleValueIndex', JSON.stringify(sampleValueIndex));

    logInfo('Sample value index built successfully', {
        correlationId: correlationId,
        indexEntries: indexEntries,
        samplesProcessed: extractedData.length
    });

} catch (e) {
    handleError(e, 'Build_Sample_Index', {
        correlationId: channelMap.get('correlationId'),
        extractedDataSize: extractedData ? extractedData.length : 0
    });
    throw e;
}

