//---------------------------------- function-------------------------------

function getRatio(evalFormula,sampleID,Well){

	//logger.info("evalFormula,sampleID,Well : " +  evalFormula +" " +  sampleID +" " + Well)
    var ratioResult = 0

           //perform ratio using values extracted in precedent step based on ID. 
           if (evalFormula && sampleID ) {
                // Extract ID references from formula (e.g., ID1 and ID6)
                var matchIDs = evalFormula.match(/ID\d+/g);
                //logger.info("matchIDs = " + matchIDs )    


            if (matchIDs && matchIDs.length >= 2) {
                    var numeratorID = parseInt(matchIDs[0].replace("ID", "")); // Convert "ID1" -> 1
				var denominatorID = parseInt(matchIDs[1].replace("ID", "")); // Convert "ID6" -> 6

				// find unique sample matching sample ID and numeratorID and position get value
				// var sample = extractedData.find(s =>  s.Sample_NTC_Control === sampleID && s.Values.configID === numeratorID).Values.value
				//logger.info("numeratorID denominatorID  " + numeratorID + " " + denominatorID)	


				var numeratorObj = extractedData.filter(s => 
						s.Sample_NTC_Control === sampleID && s.Well== Well && s.Values.some(v => v.configID == numeratorID)).map(s => s.Values.find(v => v.configID == numeratorID).Value)


				 //logger.info("numeratorObj  " + JSON.stringify(numeratorObj) )

				var denominatorObj = extractedData.filter(s => 
						s.Sample_NTC_Control === sampleID && s.Well== Well && s.Values.some(v => v.configID == denominatorID)).map(s => s.Values.find(v => v.configID == denominatorID).Value)

				// logger.info("denominatorObj  " + JSON.stringify(denominatorObj)  )

				// Extract first element of the array (if exists)
				    var numerator = numeratorObj.length > 0 ? parseFloat(numeratorObj[0]) : 0;
				    var denominator = denominatorObj.length > 0 ? parseFloat(denominatorObj[0]) : 1;

                    // Compute ratio % 
                    ratioResult = (numerator / denominator) * 100;
                }

            }
	return ratioResult

}

