// Load index once at start of step
var sampleValueIndex = JSON.parse(channelMap.get('sampleValueIndex') || '{}');
var correlationId = channelMap.get('correlationId') || 'unknown';
var ratioCalculations = 0;

try {
    logInfo('Starting Ratio calculations', {
        correlationId: correlationId,
        ratioConfigs: dbJSON.filter(function(c) { return c.MODALITY === "Ratio"; }).length
    });

    for each (var config in dbJSON) {
        if (config.MODALITY === "Ratio") {
            // find affected samples
            for each (var sample in extractedData) {
                if (sample.Target_Name === config.TARGET) {
                    // Add new value entry
                    sample.Values.push({
                        "configID": config.ID,
                        "config": config.CRITERIA,
                        "Value": getRatioOptimized(config.EVALUATION_TEXT, sample.Sample_NTC_Control, sample.Well)
                    });
                    ratioCalculations++;
                }
            }
        }
    }

    logInfo('Ratio calculations completed', {
        correlationId: correlationId,
        calculationsPerformed: ratioCalculations
    });

} catch (e) {
    handleError(e, 'Perform_Ratio', {
        correlationId: correlationId,
        calculationsCompleted: ratioCalculations
    });
    throw e;
}

