//---------------------------------- OPTIMIZED function-------------------------------

function getRatioOptimized(evalFormula, sampleID, Well) {
    var ratioResult = 0;

    try {
        // Perform ratio using values extracted in precedent step based on ID.
        if (evalFormula && sampleID) {
            // Extract ID references from formula (e.g., ID1 and ID6)
            var matchIDs = evalFormula.match(/ID\d+/g);

            if (matchIDs && matchIDs.length >= 2) {
                var numeratorID = parseInt(matchIDs[0].replace("ID", "")); // Convert "ID1" -> 1
                var denominatorID = parseInt(matchIDs[1].replace("ID", "")); // Convert "ID6" -> 6

                // OPTIMIZED: Direct O(1) index lookup instead of filter/map
                var numeratorKey = sampleID + '_' + Well + '_' + numeratorID;
                var denominatorKey = sampleID + '_' + Well + '_' + denominatorID;

                var numeratorObj = sampleValueIndex[numeratorKey];
                var denominatorObj = sampleValueIndex[denominatorKey];

                // Extract values with fallback
                var numerator = numeratorObj && numeratorObj.Value ? parseFloat(numeratorObj.Value) : 0;
                var denominator = denominatorObj && denominatorObj.Value ? parseFloat(denominatorObj.Value) : 1;

                // Compute ratio %
                ratioResult = (numerator / denominator) * 100;
            }
        }
    } catch (e) {
        logWarning('Error in getRatioOptimized', {
            correlationId: correlationId,
            evalFormula: evalFormula,
            sampleID: sampleID,
            Well: Well,
            error: e.message
        });
        ratioResult = 0;
    }

    return ratioResult;
}

