//---------------------------------- OPTIMIZED function-------------------------------

function getRefRatioOptimized(evalFormula, sampleID, Well) {
    var ratioResult = 0;

    try {
        // Perform ratio using values extracted in precedent step based on ID.
        if (evalFormula && sampleID) {
            // Extract ID references from formula (e.g., ID1/PartT)
            var matchIDs = evalFormula.match(/ID(\d+)\/([A-Za-z]+)/);

            if (matchIDs && matchIDs.length >= 2) {
                var numeratorID = parseInt(matchIDs[1].replace("ID", "")); // Convert "ID1" -> 1
                var denominatorValue = matchIDs[2]; // Convert "PartT"

                // OPTIMIZED: Direct O(1) index lookup instead of filter/map
                var numeratorKey = sampleID + '_' + Well + '_' + numeratorID;
                var numeratorObj = sampleValueIndex[numeratorKey];

                // Denominator from channelMap (already O(1))
                var denominatorObj = channelMap.get(denominatorValue);

                // Extract values with fallback
                var numerator = numeratorObj && numeratorObj.Value ? parseFloat(numeratorObj.Value) : 0;
                var denominator = denominatorObj ? parseFloat(denominatorObj) : 1;

                // Compute %
                ratioResult = (numerator / denominator) * 100;
            }
        }
    } catch (e) {
        logWarning('Error in getRefRatioOptimized', {
            correlationId: correlationId,
            evalFormula: evalFormula,
            sampleID: sampleID,
            Well: Well,
            error: e.message
        });
        ratioResult = 0;
    }

    return ratioResult;
}

