//---------------------------------- OPTIMIZED function-------------------------------

function getCtrlRatioOptimized(evalFormula, sampleID, Well) {
    var ratioResult = 0;

    try {
        // Perform ratio using values extracted in precedent step based on ID.
        if (evalFormula && sampleID) {
            // Extract ID references from formula (e.g., ID18/RC_OC43)
            var matchIDs = evalFormula.match(/ID(\d+)\/([A-Za-z]+)_([\w\W]+)/);

            if (matchIDs && matchIDs.length >= 2) {
                var numeratorID = parseInt(matchIDs[1].replace("ID", "")); // Convert "ID1" -> 1
                var denominatorControl = matchIDs[2]; // get Ctrl name (e.g., "RC")
                var denominatorTarget = matchIDs[3]; // get target name to be considered

                // OPTIMIZED: Direct O(1) index lookup for criteria
                var numeratorKey = sampleID + '_' + Well + '_' + numeratorID;
                var numeratorObj = sampleValueIndex[numeratorKey];

                if (!numeratorObj) {
                    logWarning('Numerator not found in index', {
                        correlationId: correlationId,
                        key: numeratorKey,
                        evalFormula: evalFormula
                    });
                    return 0;
                }

                var Scriteria = numeratorObj.config;

                // OPTIMIZED: Build index key for denominator (control sample)
                // Need to find configID for this criteria in control sample
                // Strategy: Search index for matching control + target + criteria
                var denominatorValue = 0;
                var denominatorFound = false;

                // Scan index for matching control entry (optimized: only checks index keys, not full array)
                for (var indexKey in sampleValueIndex) {
                    var entry = sampleValueIndex[indexKey];
                    if (entry.sampleID === denominatorControl &&
                        entry.Target === denominatorTarget &&
                        entry.config === Scriteria) {
                        denominatorValue = parseFloat(entry.Value);
                        denominatorFound = true;
                        break;
                    }
                }

                if (!denominatorFound) {
                    logWarning('Denominator control not found', {
                        correlationId: correlationId,
                        denominatorControl: denominatorControl,
                        denominatorTarget: denominatorTarget,
                        criteria: Scriteria
                    });
                    denominatorValue = 1; // Fallback
                }

                // Get numerator value
                var numerator = numeratorObj.Value ? parseFloat(numeratorObj.Value) : 0;
                var denominator = denominatorValue || 1;

                // Compute %
                ratioResult = (numerator / denominator) * 100;
            }
        }
    } catch (e) {
        logWarning('Error in getCtrlRatioOptimized', {
            correlationId: correlationId,
            evalFormula: evalFormula,
            sampleID: sampleID,
            Well: Well,
            error: e.message
        });
        ratioResult = 0;
    }

    return ratioResult;
}

// OPTIMIZATION: Helper functions for safe numeric parsing and operations

/**
 * Safely parse a float value with validation
 * @param {*} value - Value to parse
 * @param {number} defaultValue - Default value if parsing fails
 * @return {number} Parsed value or default
 */
function safeParseFloat(value, defaultValue) {
    if (value === null || value === undefined || value === '') {
        return defaultValue || 0;
    }

    var parsed = parseFloat(value);

    if (isNaN(parsed) || !isFinite(parsed)) {
        logWarning('Invalid numeric value', {
            value: value,
            defaultUsed: defaultValue || 0,
            correlationId: getCorrelationId()
        });
        return defaultValue || 0;
    }

    return parsed;
}

/**
 * Safely divide two values with validation
 * @param {*} numerator - Numerator value
 * @param {*} denominator - Denominator value
 * @param {number} defaultValue - Default value if operation fails
 * @return {number} Result or default
 */
function safeDivide(numerator, denominator, defaultValue) {
    var num = safeParseFloat(numerator, 0);
    var denom = safeParseFloat(denominator, 1);

    if (denom === 0) {
        logWarning('Division by zero', {
            numerator: numerator,
            denominator: denominator,
            defaultUsed: defaultValue || 0,
            correlationId: getCorrelationId()
        });
        return defaultValue || 0;
    }

    var result = num / denom;

    if (isNaN(result) || !isFinite(result)) {
        logWarning('Invalid division result', {
            numerator: numerator,
            denominator: denominator,
            result: result,
            defaultUsed: defaultValue || 0,
            correlationId: getCorrelationId()
        });
        return defaultValue || 0;
    }

    return result;
}

// Store functions in channelMap for use in subsequent steps
channelMap.put('helperFunctionsLoaded', 'true');

logInfo('Helper functions loaded', {
    functions: ['safeParseFloat', 'safeDivide'],
    correlationId: getCorrelationId()
});

