channel.preprocessingScript:
// REFACTORED: Preprocessing with correlation ID tracking and error handling
// This script executes once per CSV file before processing

try {
    // Initialize correlation tracking using ErrorHandler template
    var correlationId = generateUUID();
    channelMap.put('correlationId', correlationId);

    // Get file information
    var originalFilename = sourceMap.get('originalFilename');
    var fileSize = String(message).length;

    // Log file ingestion start
    logInfo('CSV file ingestion started', {
        filename: originalFilename,
        fileSize: fileSize,
        correlationId: correlationId
    });

    // Validate message is not empty
    if (!message || String(message).trim().length === 0) {
        var error = new Error('Empty CSV file');
        handleError('CSVValidation', error, {
            filename: originalFilename,
            correlationId: correlationId
        });
        throw error;
    }

    // Remove header line (first line)
    var csvLines = message.split("\n");

    if (csvLines.length < 2) {
        var error = new Error('CSV file has no data rows (only header or empty)');
        handleError('CSVValidation', error, {
            filename: originalFilename,
            lineCount: csvLines.length,
            correlationId: correlationId
        });
        throw error;
    }

    // Remove first line (header)
    csvLines.shift();

    logInfo('CSV file validated', {
        filename: originalFilename,
        dataRows: csvLines.length,
        correlationId: correlationId
    });

    // Return the cleaned CSV content
    return csvLines.join("\n");

} catch (e) {
    // Log error and rethrow to stop processing
    handleError('CSVPreprocessing', e, {
        filename: sourceMap.get('originalFilename'),
        messageLength: String(message).length
    });
    throw e; // Stop channel processing
}

channel.postprocessingScript:
// REFACTORED: Postprocessing with tracking and statistics
  // This script executes once after CSV file has been processed

  try {
      var correlationId = getCorrelationId();
      var originalFilename = sourceMap.get('originalFilename');

      // Get response status from destination connector
      // For Channel Writer destination, get the first destination response
      var destinationResponse = responseMap.get('d1'); // d1 = first destination
      var statusString = 'UNKNOWN';

      if (destinationResponse) {
          statusString = String(destinationResponse.getStatus());
      }

      // Log completion
      logInfo('CSV file processing completed', {
          filename: originalFilename,
          status: statusString,
          correlationId: correlationId
      });

      // Track successful file processing in globalMap
      if (statusString === 'SENT' || statusString === 'SUCCESS') {
          var fileCount = parseInt(globalMap.get('csv_files_processed') || '0');
          globalMap.put('csv_files_processed', String(fileCount + 1));
          globalMap.put('csv_last_processed', new Date().toISOString());
          globalMap.put('csv_last_filename', originalFilename);
      } else if (statusString === 'ERROR' || statusString === 'FAILURE') {
          var errorCount = parseInt(globalMap.get('csv_files_failed') || '0');
          globalMap.put('csv_files_failed', String(errorCount + 1));
          globalMap.put('csv_last_error', new Date().toISOString());
          globalMap.put('csv_last_error_filename', originalFilename);

          logWarning('CSV file processing failed', {
              filename: originalFilename,
              correlationId: correlationId
          });
      }

  } catch (e) {
      // Don't let postprocessing errors stop the channel
      handleError('CSVPostprocessing', e, {
          filename: sourceMap.get('originalFilename')
      });
  }

  return;

